mapper.insert:

		SELECT HKB_SEQ.NEXTVAL FROM DUAL

		INSERT INTO BROKEN_ROOM
		(
		HKB_NO
		, ROOM_ID
		, HKB_LOCATION
		, HKB_INFOR
		, HKB_EMP
		, HKB_COMPLE
		, HKB_DAY
    	)
    	VALUES
    	(
            #{hkbNo}
    		, #{roomId}
    		, #{hkbLocation}
    		, #{hkbInfor}
    		, NULL
    		, '수리예정'
    		, #{hkbDay}
   	 	)

mapper.update:
	UPDATE BROKEN_ROOM
    	SET
        	ROOM_ID = #{roomId}
        	, HKB_DAY = #{hkbDay}
        	, HKB_LOCATION = #{hkbLocation}
        	, HKB_INFOR = #{hkbInfor}
        	, HKB_EMP = #{hkbEmp}
    WHERE HKB_NO = #{hkbNo}
